import { collection, getFirestore, onSnapshot } from "firebase/firestore";
import { convertCollectionsSnapshotToMap } from "../../firebase/firebase.utils";
import shopActionTypes from "./shop.types";

export const fetchCollectionsStart = () => ({
  type: shopActionTypes.FETCH_COLLECTIONS_START,
});

export const fetchCollectionsSuccess = (collectionsMap) => ({
    type: shopActionTypes.FETCH_COLLECTIONS_SUCCESS,
    payload: collectionsMap
});

export const fetchCollectionsFailure = (errorMessage) => ({
    type: shopActionTypes.FETCH_COLLECTIONS_FAILURE,
    payload: errorMessage,
  });

export const fetchCollectionsStartAsync = async() => {
  return (dispatch) => {
    const db = getFirestore();

    dispatch(fetchCollectionsStart());

    onSnapshot(collection(db, "collections"), (snapshot) => {
      const collecttionsMap = convertCollectionsSnapshotToMap(snapshot);
      dispatch(fetchCollectionsSuccess(collecttionsMap))  ;
    }, (err) => dispatch(fetchCollectionsFailure(err.message)));
  };
};
